volumeControl1.addEventListener('input', function() {     
    gainNode1.gain.value = this.value; 
    volumeControl1.onmouseleave = function() {
        if (document.selection) // IE
        document.selection.empty();
        else
        window.getSelection().removeAllRanges();
    }
}, false);


